    private void BT_BorrarBicicletasActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BT_BorrarBicicletasActionPerformed
        String[] options = {"Yes", "No"};
        int answ = JOptionPane.showOptionDialog(null, "Esta seguro?", "Confirmar borrado", JOptionPane.YES_NO_CANCEL_OPTION, JOptionPane.QUESTION_MESSAGE, null, options, options[1]);

        if (answ == 0) {
            int index = TablaBicicletas.getSelectedRow();
            String id = TablaBicicletas.getValueAt(index, 0).toString();

            if (new Conexion().deleteBicicletas(id)) {
                JOptionPane.showMessageDialog(null, "Regstro eliminado");

                Actualiar();
            } else {
                JOptionPane.showMessageDialog(null, "Registro no eliminado, puede que existan datos asociados.");
            }

        }
    }//GEN-LAST:event_BT_BorrarBicicletasActionPerformed

