    private void BT_ActualizarBicicletasActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BT_ActualizarBicicletasActionPerformed
        // TODO add your handling code here:
        int index = TablaBicicletas.getSelectedRow();
        if (index > 0) {

            (new BicicletasAdd(
                    TablaBicicletas.getValueAt(index, 0).toString(),
                    TablaBicicletas.getValueAt(index, 1).toString(),
                    TablaBicicletas.getValueAt(index, 2).toString(),
                    TablaBicicletas.getValueAt(index, 3).toString()
            )).setVisible(true);

        }

        Actualiar();

    }//GEN-LAST:event_BT_ActualizarBicicletasActionPerformed

    private void Actualiar() {

        TablaBicicletas.setModel(new Conexion().getDataBicicletas());
    }


