    private void BT_BorrarClienteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BT_BorrarClienteActionPerformed
        String[] options = {"Yes", "No"};
        int answ = JOptionPane.showOptionDialog(null, "Esta seguro?", "Confirmar borrado", JOptionPane.YES_NO_CANCEL_OPTION, JOptionPane.QUESTION_MESSAGE, null, options, options[1]);

        if (answ == 0) {
            int index = TablaClientes.getSelectedRow();
            String fabrica = TablaClientes.getValueAt(index, 0).toString();

            if (new Conexion().deleteClientes(fabrica)) {
                JOptionPane.showMessageDialog(null, "Regstro eliminado");

                Actualiar();
            } else {
                JOptionPane.showMessageDialog(null, "Registro no eliminado, puede que existan compras  con este cliente.");
            }

        }
    }//GEN-LAST:event_BT_BorrarClienteActionPerformed

