    private void BT_ActualizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BT_ActualizarActionPerformed
        // TODO add your handling code here:
        int index = TablaClientes.getSelectedRow();
        if (index > 0) {
            // (String fecha,String contrasena, String celular,String email, String apellido, String nombre, String alias)
            (new ClientesAdd(
                    TablaClientes.getValueAt(index, 5).toString(),
                    "",
                    TablaClientes.getValueAt(index, 4).toString(),
                    TablaClientes.getValueAt(index, 3).toString(),
                    TablaClientes.getValueAt(index, 2).toString(),
                    TablaClientes.getValueAt(index, 1).toString(),
                    TablaClientes.getValueAt(index, 0).toString()
            )).setVisible(true);

        }

        Actualiar();

    }//GEN-LAST:event_BT_ActualizarActionPerformed

    private void Actualiar() {

        TablaClientes.setModel(new Conexion().getDataClientes());
    }


