    private void BT_BorrarFabricaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BT_BorrarFabricaActionPerformed
        String[] options = {"Yes", "No"};
        int answ = JOptionPane.showOptionDialog(null, "Esta seguro?", "Confirmar borrado", JOptionPane.YES_NO_CANCEL_OPTION, JOptionPane.QUESTION_MESSAGE, null, options, options[1]);

        if (answ == 0) {
            int index = TablaFabricas.getSelectedRow();
            String fabrica = TablaFabricas.getValueAt(index, 0).toString();

            if (new Conexion().deleteFabricas(fabrica)) {
                JOptionPane.showMessageDialog(null, "Regstro eliminado");

                Actualiar();
            } else {
                JOptionPane.showMessageDialog(null, "Registro no eliminado, puede que existan bicicletas con este fabricante.");
            }

        }
    }//GEN-LAST:event_BT_BorrarFabricaActionPerformed

    private void BT_AgregarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BT_AgregarActionPerformed
        // TODO add your handling code here:
        String name = JOptionPane.showInputDialog("Escriba el nombre de la fabrica");
        if (name.length() > 0) {
            if ((new Conexion()).addFabrica(name)) {
                JOptionPane.showMessageDialog(null, "Regstro agregado");
            } else {
                JOptionPane.showMessageDialog(null, "no se puede agregar el registro");
            }

        }
        Actualiar();
    }//GEN-LAST:event_BT_AgregarActionPerformed

