    private void BT_ActualizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BT_ActualizarActionPerformed
        // TODO add your handling code here:
        int index = TablaFabricas.getSelectedRow();
        if (index > 0) {
            String fabrica = TablaFabricas.getValueAt(index, 0).toString();

            String name = JOptionPane.showInputDialog("Escriba el nombre nuevo de la fabrica " + fabrica);
            if (name.length() > 0) {

                if ((new Conexion()).updateFabricas(fabrica, name)) {
                    JOptionPane.showMessageDialog(null, "Regstro actualizado");
                } else {
                    JOptionPane.showMessageDialog(null, "no se puede actualizado el registro");
                }

            }

        }

        Actualiar();

    }//GEN-LAST:event_BT_ActualizarActionPerformed

    private void Actualiar() {

        TablaFabricas.setModel(new Conexion().getDataFabricas());
    }


