    private void BT_BorrarMotocicletaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BT_BorrarMotocicletaActionPerformed
        String[] options = {"Si", "No"};
        int answ = JOptionPane.showOptionDialog(null, "Esta seguro?", "Confirmar borrado", JOptionPane.YES_NO_CANCEL_OPTION, JOptionPane.QUESTION_MESSAGE, null, options, options[1]);

        if (answ == 0) {
            int index = TablaMotocicletas.getSelectedRow();
            String id = TablaMotocicletas.getValueAt(index, 0).toString();

            if (new Conexion().deleteMotocicletas(id)) {
                JOptionPane.showMessageDialog(null, "Regstro eliminado");

                Actualiar();
            } else {
                JOptionPane.showMessageDialog(null, "Registro no eliminado, puede que existan datos asociados.");
            }

        }
    }//GEN-LAST:event_BT_BorrarMotocicletaActionPerformed

