    private void BT_ActualizarMotocicletaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BT_ActualizarMotocicletaActionPerformed
        // TODO add your handling code here:
        int index = TablaMotocicletas.getSelectedRow();
        if (index > 0) {

            (new MotocicletasAdd(
                    TablaMotocicletas.getValueAt(index, 0).toString(),
                    TablaMotocicletas.getValueAt(index, 1).toString(),
                    TablaMotocicletas.getValueAt(index, 2).toString(),
                    TablaMotocicletas.getValueAt(index, 3).toString(),
                    TablaMotocicletas.getValueAt(index, 4).toString(),
                    TablaMotocicletas.getValueAt(index, 5).toString()
            )).setVisible(true);

        }

        Actualiar();

    }//GEN-LAST:event_BT_ActualizarMotocicletaActionPerformed

    private void Actualiar() {

        TablaMotocicletas.setModel(new Conexion().getDataMotocicletas());
    }


