    private void BT_BorrarProveedorActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BT_BorrarProveedorActionPerformed
        String[] options = {"Yes", "No"};
        int answ = JOptionPane.showOptionDialog(null, "Esta seguro?", "Confirmar borrado", JOptionPane.YES_NO_CANCEL_OPTION, JOptionPane.QUESTION_MESSAGE, null, options, options[1]);

        if (answ == 0) {
            int index = TablaProveedores.getSelectedRow();
            String prov_id = TablaProveedores.getValueAt(index, 0).toString();

            if (new Conexion().deleteProveedor(prov_id)) {
                JOptionPane.showMessageDialog(null, "Regstro eliminado");

                Actualiar();
            } else {
                JOptionPane.showMessageDialog(null, "Registro no eliminado, puede que existan datos asociados.");
            }

        }
    }//GEN-LAST:event_BT_BorrarProveedorActionPerformed

