    private void BT_ActualizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BT_ActualizarActionPerformed
        // TODO add your handling code here:
        int index = TablaProveedores.getSelectedRow();
        if (index > 0) {
            // 
            (new ProveedorAdd(
                    TablaProveedores.getValueAt(index, 0).toString(),
                    TablaProveedores.getValueAt(index, 1).toString(),
                    TablaProveedores.getValueAt(index, 2).toString(),
                    TablaProveedores.getValueAt(index, 3).toString()
            )).setVisible(true);

        }

        Actualiar();

    }//GEN-LAST:event_BT_ActualizarActionPerformed

    private void Actualiar() {

        TablaProveedores.setModel(new Conexion().getDataProveedor());
    }


